INSERT INTO `about_page` (`page_id`, `page_title`, `page_menu_title`, `page_image`, `page_text`, `page_author`, `page_pushtime`, `page_blank`, `page_menu_status`, `page_type`, `page_status`, `page_order`, `page_index`, `page_tpl`, `dohtml`, `dosmiley`, `doxcode`, `doimage`, `dobr`) VALUES
(1, '简介', 'XOOPS', '', '<p><a href="http://xoops.org.cn/" class="external text" title="http://xoops.org.cn" rel="nofollow">XOOPS</a>是一个开放源代码的工具程序， 用于实现从小型到大型的动态交互社区，公司门户，企业内部网，网络日  志等。安装到服务器主机后，网站管理员可以登陆到管理区，通过浏览器来管理内容信息。后台管理手段直观易用，不需要任何编  程，而且XOOPS还提供了方便的安装程序。XOOPS是一个软件包，是一个网站群和一个遍布世界、非常友善的支持社区。而将这三  者紧密联系在一起的是WEB-互联网的图形界面。</p>\r\n<p>XOOPS是一个管理动态站点的工具。这个简单的定义可以让您初步领会到此出色软件包的用处。但它是如此笼统相当于啥也没说。  让我们再尝试定义一下：XOOPS是一个开放源代码的用PHP语言编写的脚本集，在安装到一个支持PHP语言、有像apache一样的web服  务器、有mysql数据库的互联网主机上后，它可以轻松创建动态的、复杂的以及内容丰富的web站点，从简单如个人blog到复杂如公司  门户。</p>\r\n<p>XOOPS意思是可扩展面向对象门户系统（eXtensible Object Oriented Portal System），虽然有些人把它叫做&ldquo;ex-oh-oh-pi-es&rdquo;，实际上XOOPS  的标准发音更像&ldquo;zoops&rdquo;。</p>\r\n<p>XOOPS起源于其它的开放源代码项目，它是古老的&ldquo;PHPNuke&rdquo;的后代，衍生于其一个分支    &ldquo;myPHPNuke&rdquo;。其大部分都被彻底重写，虽  然在脚本里面仍然存在着一些古老的代码。</p>\r\n<p>XOOPS主要面向社区站点的建设，但它足够灵活可以用于单管理员站点（如blog）或者企业面向内容的门户站点。</p>\r\n<p>相对于其他 CMS，XOOPS 有完善的权限管理，可以对不同用户群组赋予不同的使用或管理权限。XOOPS 代码规范，注重安全性，极  少被发现有安全漏洞；结构设计合理，易于作二次开发。作为理想的开发系统，被广泛用于个人网站、社区交流、公司企业以及各  类大型门户系统。</p>\r\n<p>XOOPS 社区开发活跃，各类功能模块可有不同选择，包括：新闻发布系统、文章管理、论坛、相册、资源下载、友情链接、广告/黄  页管理、在线购物等传统模块，近来并有各种新型类别如 BLOG、WIKI、RSS 在线阅读/采集等。</p>\r\n<p>作为一个功能强大的 CMS 系统，得益于其结构设计合理，XOOPS 除了本身的各类功能模块之外，可以很方便的集成现有的软件，比  著名的 BLOG 程序 WORDPRESS、WIKI管理MEDIAWIKI、购物系统 OSCOMMERCE/ZEN-CART、图片管理程序 GALLERY等已经被集成到XOOPS 中。</p>\r\n<p>XOOPS 是自由软件，按照 GPL (GNU General Public License) 协议发布，在遵守GPL条款的前提下可以在任何场合免费使用和修改。</p>', '1', 1266832354, 0, 1, 1, 1, 1, 1, 'default', 1, 0, 0, 0, 0),
(2, '北京众锐普斯信息技术有限公司', '众锐普斯', '', '<p><a href="http://www.xoops.com.cn/">北京众锐普斯信息技术有限公司</a>（下文简称&ldquo;众锐普斯&rdquo;）成立于2007年3月，旨在以开源项目XOOPS为基础，为客户提供专业的技术开发定制服务，并探 索开源软件在国内的开发和应用模式，推动开源项目在国内的发展。</p>\r\n<p>众锐普斯主要由毕业自清华大学、北京大学、中国人民大学以及中科院的博士、硕士等组成。在技术方面，既有XOOPS国际项目的主开发和管理员，也有 Freebsd等操作系统的专家；在网络优化方面，既有专业的用户界面设计师，又有信息资源管理专家；在管理方面，公司成员不仅具有大型国际商业公司和投 资公司的从业背景，更有丰富的项目管理实战经验。</p>\r\n<p>长期以来，众锐普斯的成员一直是XOOPS开源社区的活跃成员和重要贡献者。</p>\r\n<h4 id="about_index_content_title">联系方式</h4>\r\n<p>北京众锐普斯信息技术有限公司<span style="border-collapse: separate; color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px;" class="Apple-style-span"><span style="font-family: Arial,Verdana,sans-serif; font-size: 12px;" class="Apple-style-span">\r\n<ul class="mainTextColor">\r\n    <li>电&nbsp;&nbsp;&nbsp;&nbsp;话： 86 010 62766391</li>\r\n    <li>传&nbsp;&nbsp;&nbsp;&nbsp;真： 86 010 62766391</li>\r\n    <li>地&nbsp;&nbsp;&nbsp;&nbsp;址： 中国 北京 北京市东城区安定路20号院&nbsp; 燕都商务5号楼253室</li>\r\n    <li>公司网址：<a href="http://www.xoops.com.cn/">http://www.xoops.com.cn/</a></li>\r\n    <li>XOOPS中文社区：<a href="http://www.xoops.org.cn/">http://www.xoops.org.cn/</a></li>\r\n</ul>\r\n</span></span></p>', '1', 1266832389, 0, 1, 1, 1, 3, 0, 'default', 1, 0, 0, 0, 0);
