CREATE TABLE `res_resources` (`res_id` int(11) NOT NULL auto_increment,`cat_id` int(8) NOT NULL  default '0',`uid` int(8) NOT NULL default '0',`res_subject` varchar(255) NOT NULL default '',`res_content` text,`res_attachment` int(8) NOT NULL default '0',`res_status` tinyint(1) NOT NULL default '0',`res_weight` INT( 3 ) NOT NULL DEFAULT '0',`grate_time` int(10) NOT NULL DEFAULT '0',`update_time` int(10) NOT NULL DEFAULT '0',`dohtml` tinyint(1) NOT NULL default '1',`dosmiley` tinyint(1) NOT NULL default '0',`doxcode` tinyint(1) NOT NULL default '0',`doimage` tinyint(1) NOT NULL default '0',`dobr` tinyint(1) NOT NULL default '0',`res_comments` int(10) NOT NULL default '0',`res_counter` int (11) NOT NULL default '0' ,`res_rating` int( 10 ) NOT NULL default '0' ,`res_rates` int( 10 ) NOT NULL default '0' ,PRIMARY KEY  (`res_id`),KEY `cat_id` (`cat_id`,`uid`)) ENGINE=MyISAM;CREATE TABLE `res_attachments` (`att_id` int(11) NOT NULL auto_increment,`res_id` int(11) NOT NULL default '0',`uid` int(8) NOT NULL default '0',`att_subject` varchar(255) NOT NULL default '',`att_content` text,`att_filename` varchar(255) NOT NULL,`att_attachment` varchar(255) NOT NULL,`att_type` varchar(128) NOT NULL default '',`att_size` int(11) NOT NULL default '0',`att_downloads` int(11) NOT NULL default '0',`grate_time` int(10) NOT NULL default '0',`update_time` int(10) NOT NULL default '0',PRIMARY KEY  (`att_id`),KEY `res_id` (`res_id`,`uid`)) ENGINE=MyISAM;CREATE TABLE `res_category` (`cat_id` int(8) NOT NULL auto_increment,`cat_name` varchar(255) NOT NULL default '',`cat_description` text,`cat_image` varchar(255) NOT NULL default '',`cat_status` tinyint(1) NOT NULL default '0',`cat_weight` INT( 2 ) NOT NULL DEFAULT '0',`grate_time` int(10) NOT NULL DEFAULT '0',`update_time` int(10) NOT NULL DEFAULT '0',PRIMARY KEY  (`cat_id`)) ENGINE=MyISAM ;CREATE TABLE `res_link` (`link_id` INT( 11 ) NOT NULL AUTO_INCREMENT ,`res_id` INT( 11 ) NOT NULL DEFAULT '0',`uid` INT( 11 ) NOT NULL DEFAULT '0',`mod_name` varchar(255) NOT NULL default '',`id` INT( 11 ) NOT NULL DEFAULT '0',PRIMARY KEY ( `link_id` )) ENGINE=MyISAM ;CREATE TABLE `res_rate` (`rate_id` int( 11 ) NOT NULL AUTO_INCREMENT ,`res_id` int( 11 ) NOT NULL default '0' ,`uid` int( 11 ) NOT NULL default '0' ,`rate_rating` int( 11 ) NOT NULL default '0' ,`rate_time` int( 10 ) NOT NULL default '0' ,	PRIMARY KEY ( `rate_id` )) ENGINE=MyISAM ;CREATE TABLE `res_counter` (`counter_id` 		INT( 11 ) 	 NOT NULL auto_increment,`res_id` 		INT( 11 ) 		NOT NULL default '0',	`uid` 			mediumint(8) 	 NOT NULL default '0',`ip` 	INT( 11 )		NOT NULL default '0',`counter_time` 		int(10) 	 NOT NULL default '0',PRIMARY KEY  		(`counter_id`)) TYPE=MyISAM;